import { useEffect, useState } from 'react';
// import io from 'socket.io-client';
import socketIOClient from 'socket.io-client';
const ENDPOINT = "http://127.0.0.1:4000";

function App() {
  const [response, setResponse] = useState("");

  useEffect(() => {
    const socket = socketIOClient(ENDPOINT);
    socket.on("FromServer", data => {
      setResponse(data);
    });
  });
  // const socket = io(); // no se pasa argumento de url pues por defecto conecta al localhost

  // socket.on('message-server', (msg) => {
  //   console.log('Mensaje del servidor');
  // });

  // const sendMesssageToServer = () => {
  //   socket.emit('msg-from-browser', 'Mensaje enviado del browser ...');
  // }

  return (
    <>
      <h1>Componente App</h1>
      <p>
        Mensaje del servidor via socket: {response}
      </p>
    </>
  );
}

export default App;
