//import logo from './logo.svg';
//import './App.css';

import io from 'socket.io-client';

function App() {
  const socket = io(); // no se pasa argumento de url pues por defecto conecta al localhost

  // socket.on('message-server', (msg) => {
  //   console.log('Mensaje del servidor');
  // });

  const sendMesssageToServer = () => {
    socket.emit('msg-from-browser', 'Mensaje enviado del browser ...');
  }

  return (
    <>
      <h1>Componente App</h1>
      <button onClick={sendMesssageToServer}>Enviar mensaje al servidor por socket</button>
    </>
  );
}

export default App;
